import {EventEmitter, Injectable} from '@angular/core';
import {Recipe} from './recipe.model';
import {IngredientModel} from '../shared/ingredient.model';
import {ShoppingListService} from '../shopping-list/shopping-list.service';

@Injectable()
export class RecipeService {
  recipeSelected = new EventEmitter <Recipe> ();
  private recipes: Recipe [] = [new Recipe('Test name',
    'tesst  description',
    // tslint:disable-next-line:max-line-length
    'data:image/jpeg;base64,',
    [new IngredientModel('meat', 1), new IngredientModel('pear', 1)]),
    new Recipe('Test2 name',
      'tesst  description',
      // tslint:disable-next-line:max-line-length
      'data:image/jpeg;base64,',
      [new IngredientModel('meat', 3), new IngredientModel('pear', 3)])];
  constructor(private slService: ShoppingListService) {}
  getRecipes() {
    return this.recipes.slice();
  }
  addToShoppingList(ingredients: IngredientModel[]) {
    this.slService.addIngredients(ingredients);
  }
}
